<script type="text/javascript">
  /**
   * hasClass
   *
   * @return boolean
   */
  function hasClass(element, elClass) {
    return element === null ? false : element.classList.contains(elClass);
  }

  /**
   * addClass
   *
   * @param element
   * @param elClass
   * @return void
   */
  function addClass(element, elClass) {
    element.classList.add(elClass);
  }

  /**
   * removeClass
   *
   * @param element
   * @param elClass
   * @return void
   */
  function removeClass(element, elClass) {
    element.classList.remove(elClass);
  }

  /**
   * toggleClass
   *
   * @param element
   * @param elClass
   * @return void
   */
  function toggleClass(element, elClass) {
    hasClass(element, elClass) ? removeClass(element, elClass) : addClass(element, elClass);
  }

  function queue(id, delay, repeat) {
    setTimeout(function() {
      var el = document.getElementById(id);

      if(hasClass(el, 'walk')) {
        removeClass(el, 'walk');
      }

      addClass(el, 'walk');
    }, delay);

//    if(repeat) {
//      setInterval(function() {
//        var el = document.getElementById(id);
//
//        toggleClass(el, 'walk');
//      }, delay+15000);
//    }

  }

  function walk() {
    queue('pair-1', 0, true);
    queue('pair-2', 10000, true);
  }

  function checkVisible(elm) {
    if(!elm) return;

    var rect = elm.getBoundingClientRect();
    var viewHeight = Math.max(document.documentElement.clientHeight, window.innerHeight);
    return !(rect.bottom < 0 || rect.top - viewHeight >= 0);
  }

  window.addEventListener('scroll', function() {
    if(checkVisible(document.getElementById('location-video-full'))) {
      queue('pair-3', 0, false);
    }
  });

  walk();
</script>
